  static List<Sinif> get tumSiniflar => _tumSiniflar;

  static Future<List<Soru>> getSorular(String sinifAdi) async {
    var bulunanSorular = <Soru>[];
    // Ana kategorileri dolaş
    for (final sinif in _tumSiniflar) {
      // Eğer ana kategori ismi eşleşiyorsa
      if (sinif.sinifAdi == sinifAdi) {
        bulunanSorular = sinif.sorular;
        break;
      }
      // Ana kategorinin alt kategorilerinde ara
      for (final altKategori in sinif.altKategoriler) {
        if (altKategori.sinifAdi == sinifAdi) {
          bulunanSorular = altKategori.sorular;
          break;
        }
      }
    }
    return Future.value(bulunanSorular);
  }

  static Future<List<Soru>> getKarisikSorular() async {
    final tumSorular = <Soru>[];
    // Tüm sınıfları ve alt kategorileri dolaşarak soruları topla
    for (final sinif in _tumSiniflar) {
      tumSorular.addAll(sinif.sorular);
      for (final altKategori in sinif.altKategoriler) {
        tumSorular.addAll(altKategori.sorular);
      }
    }
    // Tüm soruları karışık bir şekilde sırala
    tumSorular.shuffle();
    // İlk 100 soruyu al
    return Future.value(tumSorular.take(10000).toList());
  }
